export const QUESTION = 2,
  DEFINITIONS = 3,
  RANGES = 4,
  SET_RANGE = 6,
  CORRECT = 7,
  INCORRECT = 8;

export class Range {
  constructor() {
    this.start = "";
    this.end = "";
    this.step = "";
    this.fullRange = function () {
      if (
        this.start.trim() == "" ||
        this.end.trim() == "" ||
        this.step.trim() == ""
      ) {
        return "";
      }

      return "(" + this.start + "," + this.end + "," + this.step + ")";
    };
  }

  setFullRange(range) {
    let [s, e, p] = range.split(",", 3);
    s = s.substring(1);
    p = p.slice(0, -1);

    this.start = s;
    this.end = e;
    this.step = p;
  }

  updateRange() {
    this.fullRange = "(" + this.start + "," + this.end + "," + this.step + ")";
  }
}

// Class that represents the
// variable type variable for tasks
export class variableInfo {
  constructor(name) {
    this.name = name;
    this.definition = "";
    this.range = "";

    this.value = 0; // value

    this.testRange = new Range();
    this.visible = true;
  }
}

// Class that represents the
// answer type variable for tasks
export class answerInfo {
  constructor(name) {
    this.name = name;
    this.correct = [];
    this.incorrect = [];

    // values
    this.correct_values = [];
    this.incorrect_values = [];

    this.visible = true;
  }
}

// Class that represents the
// questions for a task
export class taskQuestion {
  constructor() {
    this.text = "";
    this.answers = new Map();
  }
}

