import { START, TEXT, VARIABLE, ANSWER, tokenize } from "./tokenizer"

export const FILENAME1 = 'test.txt';
export const FILENAME2 = 'test4.txt';

//console.dir(tokenize(FILENAME1), {'maxArrayLength': null});

export const QUESTION = 2, DEFINITIONS = 3, RANGES = 4, SET_RANGE = 6, CORRECT = 7, INCORRECT = 8;

// Class that represents the
// variable type variable for tasks
export class variableInfo 
{
    constructor(name) 
    {
        this.name = name;
        this.definition = "";
        this.range = "";

        this.value = 0; // value
    }
}

// Class that represents the 
// answer type variable for tasks
export class answerInfo 
{
    constructor(name) {
        this.name = name;
        this.correct = [];
        this.incorrect = [];

        // values
        this.correct_values = []
        this.incorrect_values = []
    }
}

// Class that represents the 
// questions for a task
export class taskQuestion 
{
    constructor() {
        this.text = "";
        this.answers = new Map();
    }
}

// Class that represents the task
// this will be saved in the database
