// Parses the markup language
export function parseMathMarkup(file_name) 
{
    // First, tokenize the input file
    let tokens = tokenize(file_name);

    // Create variable where the new task
    // will be saved
    //current_task = new Task()

    let variables = new Map();
    variables.set('task-text', '');
    let c_name = "";
    let q_num = 0;

    var PART = START;

    for ( let i = 0; i < tokens.length; i++ ) {

        let token = tokens[i];
        let token2 = (i + 1 == tokens.length) ? tokens[i] : tokens[i+1];

        switch(PART) 
        {
            case START : {
                if ( token.type == 'keyword' && token.value == '#text' ) 
                    { PART = TEXT; break; }

                if ( token.type == 'keyword' && token.value == '#question' )
                    { 
                        PART = QUESTION; 
                        q_num++;
                        variables.set('q'+ q_num.toString(), new taskQuestion() );
                        break;
                    }

                if ( token.type == 'keyword' && token.value == '#definitions' ) 
                    { PART = DEFINITIONS; break; }

                if ( token.type == 'keyword' && token.value == '#ranges' ) 
                    { PART = RANGES; break; }

                if ( token.type == 'newline' ) break;

                break;
            }

            case TEXT : {

                if ( token.type != 'newline' ) {

                    let txt = variables.get('task-text');
                    txt += token.value + ' ';
                    variables.set('task-text', txt);

                }

                if ( token2.type == 'keyword' ) {
                    PART = START;
                    break;
                }
                break;
            }

            case QUESTION : {

                // add token to question text
                let q = variables.get('q' + q_num.toString());
                if(token.type != 'newline') {
                    q.text += token.value + ' ';
                }
                variables.set('q' + q_num.toString(), q)

                if ( token2.type == 'keyword' ) {
                    PART = START;
                    break;
                }

                break;
            }

            case DEFINITIONS : {
                if ( token.type == 'var' && token2.type == 'eq' ) {
                    variables.set(token.value, new variableInfo(token.value));
                    c_name = token.value;
                    PART = VARIABLE;
                    i = i + 1;
                    break;
                }

                if ( token.type == 'var' && token2.type == 'newline' ) {
                    variables.set(token.value, new variableInfo(token.value));
                    i = i + 1;
                    break;
                }

                if ( token.type == 'answer' ) {
                    let q = variables.get('q' + q_num.toString());
                    c_name = token.value;

                    if(q.answers.get(token.value) == undefined)
                    {
                        q.answers.set(token.value, new answerInfo(token.value));
                    }

                    variables.set('q' + q_num.toString(), q);
                    c_name = token.value;
                    PART = ANSWER;
                    break;
                }

                if ( token2.type == 'keyword' ) {
                    PART = START;
                    break;
                }  

                break;
            }

            case VARIABLE : {

                let info = variables.get(c_name);
                info.definition += token.value;
                variables.set(c_name, info);

                if ( token2.type == 'newline' ) {
                    PART = DEFINITIONS;
                    break;
                }

                break;
            }

            case ANSWER : {

                if ( token2.type == 'newline' ) {
                    PART = DEFINITIONS;
                    break;
                }

                if(token.type == 'accessor' && token.value == '.correct') {
                    PART = CORRECT;
                    break;
                }

                else if(token.type == 'accessor' && token.value == '.incorrect') {
                    PART = INCORRECT;
                    break;
                }

                break;
            }

            case CORRECT: {

                if ( token2.type == 'r-bracket') {
                    PART = DEFINITIONS;
                    break;
                }

                if(token.type == 'eq' || token.type == 'newline') break;

                let q = variables.get('q' + q_num.toString() );
                let ans = q.answers.get(c_name); 

                if(token.type == 'l-bracket' || token.type == 'comma')
                {
                    ans.correct.push('');
                    q.answers.set(c_name, ans);
                    variables.set('q' + q_num.toString(), q)
                    break;
                }

                let val = ans.correct.at(-1);
                val += token.value;
                ans.correct[ans.correct.length - 1] = val;

                q.answers.set(c_name, ans);
                variables.set('q' + q_num.toString(), q)

                if ( token2.type == 'r-bracket') {
                    PART = DEFINITIONS;
                    break;
                }

                break;
            }

            case INCORRECT: {

                if ( token2.value == ']') {
                    PART = DEFINITIONS;
                    break;
                }

                if(token.type == 'eq' || token.type == 'newline')
                {
                    break;
                }

                let q = variables.get('q' + q_num.toString() );
                let ans = q.answers.get(c_name);

                if(token.type == 'l-bracket' || token.type == 'comma')
                {
                    ans.incorrect.push('');
                    q.answers.set(c_name, ans);
                    variables.set('q' + q_num.toString(), q)
                    break;
                }

                let val = ans.incorrect.at(-1);
                val += token.value;

                ans.incorrect[ans.incorrect.length - 1] = val;

                q.answers.set(c_name, ans);
                variables.set('q' + q_num.toString(), q)

                break;
            }

            case RANGES : 
            {
                if ( token.type == 'var' ) {
                    c_name = token.value;
                    PART = SET_RANGE;
                    i = i + 2;
                    break;
                }  

                if ( token2.type == 'newline' ) {
                    PART = START;
                }
                break;
            }

            case SET_RANGE :
            {
                let info = variables.get(c_name);
                info.range += token.value;
                variables.set(c_name, info);

                if ( token2.type == 'newline' ) {
                    PART = RANGES;
                    break;
                }

                break;
            }
        }
    }

    return variables;
}

