//const fs = require('fs');       // import fs module
//const math = require('mathjs'); // import math.js module

// Simple token class
export class Token
{
    constructor(type, value) 
    {
        this.type = type;
        this.value = value;
    }
}

// Automata states
export const START = 0, VARIABLE = 11, KEYWORD = 22, ANSWER = 33, TEXT = 44;

/*
    Create tokens from the input text.
    Input: file_name - markup saved in text file
    Output: tokens - array of tokens
*/
export function tokenize(data)
{    
    // Get input text
    //var data = fs.readFileSync(file_name, 'utf-8');
    /* var data = `#text Spočítajte. 

    #question $x1 + $x2 = @r 

    #definitions 
    $x1 
    $x2 

    @r.correct = [
        $x1 + $x2
    ]
    @r.incorrect = [
        $x1 + $x2 + 1,
        $x1 + $x2 - 1,
        $x1 - $x2
    ]

    #ranges
    $x1 = range(0, 100, 1)
    $x2 = range(0, 100, 1)`; */

    // Initialize buffer and tokens array
    var buffer = "", tokens = [];

    var STATE = START;
    for ( var i = 0; i < data.length; i++ ) {

        var character = data[i];

        //console.log( character );

        switch ( STATE ) 
        {
            case START:
            {
                // 'Append' first character
                buffer = character;

                // Analyze character
                switch ( character )
                {
                    case '$': { STATE = VARIABLE; break; }

                    case '#': { STATE = KEYWORD; break; }

                    case '@': { STATE = ANSWER; break; }

                    case ' ': break;
                    case '\t': break;
                    case '\n': {  tokens.push( new Token('newline', character)); break; }

                    case '(': {  tokens.push( new Token('l-parenth', character)); break; }
                    case ')': {  tokens.push( new Token('r-parenth', character)); break; }
                    case '[': {  tokens.push( new Token('l-bracket', character)); break; }
                    case ']': {  tokens.push( new Token('r-bracket', character)); break; }
                    case '+': {  tokens.push( new Token('add', character)); break; }
                    case '-': {  tokens.push( new Token('sub', character)); break; }
                    case '/': {  tokens.push( new Token('div', character)); break; }
                    case '*': {  tokens.push( new Token('mul', character)); break; }
                    case ',': {  tokens.push( new Token('comma', character)); break; }
                    case '=': {  tokens.push( new Token('eq', character)); break; }
                    case '^': {  tokens.push( new Token('caret', character)); break; }

                    default: 
                    { 
                        // Lookahead for the next character
                        if ( /[\s$#@()+\-\^/*,=\[\]]/.test( data[i+1] ) ) { // eslint-disable-line

                            if ( ! isNaN( +buffer ) ) 
                            {
                                tokens.push(new Token('number', buffer));
                            }
                            else 
                            { 
                                tokens.push(new Token('text', buffer));
                            }

                            break;
                        }

                        STATE = TEXT; 
                        break;
                    }
                }   
                break;
            }

            // Handle variabes ( starting with $ )
            case VARIABLE: 
            {
                // Test current character
                if ( /[a-zA-Z0-9\_]/.test( character ) ) // eslint-disable-line
                {
                    buffer += character;

                    // Lookahead to determine next token  
                    if ( !( /[a-zA-Z0-9\_]/.test(data[i+1]) ) ) // eslint-disable-line
                    {
                        tokens.push(new Token('var', buffer));
                        STATE = START;
                        break;
                    }

                    STATE = VARIABLE;
                    break; 
                }
                else 
                {
                    tokens.push(buffer);
                    STATE = START;
                    break;
                }
            }

            // Handle keywords ( starting with # )
            case KEYWORD: 
            {
                // Test current character
                if ( /[a-zA-Z]/.test( character ) ) // eslint-disable-line
                {
                    buffer += character;

                    // Lookahead to determine next token
                    if ( !( /[a-zA-Z]/.test(data[i+1]) ) ) // eslint-disable-line
                    {
                        tokens.push(new Token('keyword', buffer));
                        STATE = START;
                        break;
                    }

                    STATE = KEYWORD;
                    break;
                }
                else
                {
                    tokens.push(new Token('hash', buffer));
                    STATE = START;
                    break;
                }
            }

            // Handle answer ( starts with @ )
            case ANSWER:
            {
                // Test current character 
                if ( /[a-zA-Z0-9\_]/.test( character ) ) // eslint-disable-line
                {
                    buffer += character;

                    // Lookahead to determine next token
                    if ( !( /[a-zA-Z0-9\_]/.test(data[i+1]) ) ) // eslint-disable-line
                    {    
                        tokens.push(new Token('answer', buffer));
                        STATE = START;
                        break;
                    }

                    STATE = ANSWER;
                    break; 
                }
                else 
                {
                    tokens.push(new Token('at', buffer));
                    STATE = START;
                    break;
                }
            }

            // Handle text
            case TEXT: 
            {
                // Lookahead for next character
                // determine whether there are more character to the text or not
                // if any of these return true then text ended
                if( /[\s$#@()+\-\^/*,=\[\]]/.test( data[i+1] ) ) // eslint-disable-line
                {
                    buffer += character;

                    // check if we hit a number
                    if ( ! isNaN( +buffer ) ) 
                    {
                        tokens.push(new Token('number', buffer));
                    }
                    // check if we hit accessor to an answer
                    else if ( buffer[0] == '.' ) 
                    {
                        tokens.push(new Token('accessor', buffer));
                    } 
                    // we ended a text section
                    else 
                    {
                        tokens.push(new Token('text', buffer));
                    }

                    STATE = START;
                    break;
                }

                buffer += character;
                break;
            }
        }
    }

    return tokens;
}
